mapper.select:
select py_no, m_no, py_name, py_price, py_period, py_type, py_date
from payment
where py_no= #{pyNo}

select py_no, m_no, py_name, py_price, py_period, py_type, py_date
from payment
where m_no= #{mNo}

mapper.update:
update company
set co_check='Y'
where m_no = #{mNo}

update product
set p_check = 'Y'
where m_no= #{mNo}

update company
set co_check='N'
where m_no = #{mNo}

update product
set p_check = 'N'
where m_no= #{mNo}
